DROP FUNCTION IF EXISTS render_text_as_geometry;
CREATE OR REPLACE FUNCTION render_text_as_geometry(
    input_text TEXT,
    scale_factor DOUBLE PRECISION DEFAULT 1.0,
    font_type TEXT DEFAULT 'LINES',
    base_geom GEOMETRY DEFAULT NULL,
    text_position INTEGER DEFAULT NULL,
    max_height DOUBLE PRECISION DEFAULT NULL
)
RETURNS GEOMETRY
LANGUAGE plpgsql
AS $$
DECLARE
    expanded_text TEXT;
    letter_arr TEXT[];
    pos_arr INT[];
    current_letter TEXT;
    pos INT;
    raw_widths DOUBLE PRECISION[];
    raw_width DOUBLE PRECISION;
    total_raw_width DOUBLE PRECISION;
    final_scale DOUBLE PRECISION;
    height_scale DOUBLE PRECISION;
    scaled_geoms GEOMETRY[];
    widths DOUBLE PRECISION[];
    x_offsets DOUBLE PRECISION[];
    acc_width DOUBLE PRECISION := 0;
    i INT;
    glyph_geom GEOMETRY;
    placed_geoms GEOMETRY[];
    result_geom GEOMETRY;
    geom_type_name TEXT;
    start_pt GEOMETRY;
    end_pt GEOMETRY;
    center GEOMETRY;
    angle DOUBLE PRECISION;
    tg_centroid GEOMETRY;
    raw_height DOUBLE PRECISION;
BEGIN

    CALL font_source(TRUE); -- Turn on / Create font table

    -- Return NULL if geometry is NULL or input_text contains only unavailable characters to be rendered
	IF ( 
	     base_geom IS NULL OR
	     input_text IS NULL OR
		 NOT EXISTS (
                      SELECT 1
                      FROM regexp_split_to_table(input_text, '') AS ch
                      WHERE ch IN (SELECT letter FROM public.font_character_for_rendering)
                    )
	   )
	THEN
       CALL font_source(FALSE); -- Turn off / Drop font table
	   RETURN NULL; 
	END IF;

    -- Step 1: Expand text with separator characters | note: chr(0x2063) is an empty character
    expanded_text := '';
    FOR i IN 1..char_length(input_text) LOOP
        expanded_text := expanded_text || substr(input_text, i, 1);
        IF i < char_length(input_text) THEN
            expanded_text := expanded_text || chr(0x2063);
        END IF;
    END LOOP;

    -- Step 2: Split into array
    letter_arr := regexp_split_to_array(expanded_text, '');
    pos_arr := ARRAY(SELECT generate_series(1, array_length(letter_arr, 1)));

    -- Step 3: Load font geometries and compute raw widths
    raw_widths := ARRAY[]::DOUBLE PRECISION[];
    scaled_geoms := ARRAY[]::GEOMETRY[];
    FOR i IN 1..array_length(letter_arr, 1) LOOP
        current_letter := letter_arr[i];
        SELECT f.geom, (ST_XMax(f.geom) - ST_XMin(f.geom)) INTO glyph_geom, raw_width
        FROM public.font_character_for_rendering f
        WHERE f.letter = current_letter;

        raw_widths := raw_widths || raw_width;
    END LOOP;

    -- Step 4: Compute total width
    SELECT SUM(x) INTO total_raw_width FROM unnest(raw_widths) AS x;

    -- Step 5: Compute scale factor based on base_geom
    geom_type_name := ST_GeometryType(base_geom);

    IF geom_type_name = 'ST_LineString' AND ST_Length(base_geom) > 0 THEN
        final_scale := scale_factor * (ST_Length(base_geom) * 1) / GREATEST(total_raw_width, 1e-6); -- Replace 1 to any value if necessary 0.8
    ELSE
        final_scale := scale_factor;
    END IF;

    -- Adjust scale factor based on max_height constraint
    IF max_height IS NOT NULL THEN
        SELECT MAX(ST_YMax(g) - ST_YMin(g)) INTO raw_height
        FROM (
            SELECT ST_Transform(ST_SetSRID(f.geom, 4326), 3857) AS g
            FROM public.font_character_for_rendering f
            WHERE f.letter = ANY(letter_arr)
        ) AS transformed_chars;
    
        IF raw_height > 0 THEN
            height_scale := ( scale_factor * max_height / raw_height ) * 2.3; -- 2.3 is arbitrary to improve final height
            final_scale := LEAST(final_scale, height_scale);
        END IF;
    END IF;

    -- Step 6: Apply scaling and measure scaled widths
    widths := ARRAY[]::DOUBLE PRECISION[];
    FOR i IN 1..array_length(letter_arr, 1) LOOP
        current_letter := letter_arr[i];
        SELECT ST_Scale(f.geom, final_scale, final_scale),
               (ST_XMax(f.geom) - ST_XMin(f.geom)) * final_scale
        INTO glyph_geom, raw_width
        FROM public.font_character_for_rendering f
        WHERE f.letter = current_letter;

        scaled_geoms := scaled_geoms || glyph_geom;
        widths := widths || raw_width;
    END LOOP;

    -- Step 7: Calculate x offsets
    x_offsets := ARRAY[]::DOUBLE PRECISION[];
    acc_width := 0;
    FOR i IN 1..array_length(widths, 1) LOOP
        x_offsets := x_offsets || acc_width;
        acc_width := acc_width + widths[i];
    END LOOP;

    -- Step 8: Translate characters to their positions
    placed_geoms := ARRAY[]::GEOMETRY[];
    FOR i IN 1..array_length(letter_arr, 1) LOOP
        IF letter_arr[i] <> chr(0x2063) AND letter_arr[i] <> ' ' THEN
            placed_geoms := placed_geoms || ST_Translate(scaled_geoms[i], x_offsets[i], 0);
        END IF;
    END LOOP;

    -- Step 9: Combine into one geometry
    SELECT ST_Collect(g) INTO result_geom FROM unnest(placed_geoms) AS g;

    -- Step 10: Alignment info
    SELECT ST_StartPoint(base_geom), ST_EndPoint(base_geom), ST_Centroid(base_geom), ST_Azimuth(ST_StartPoint(base_geom), ST_EndPoint(base_geom))
    INTO start_pt, end_pt, center, angle;
	--RAISE NOTICE 'start_pt = %', ST_AsText(start_pt);
	--RAISE NOTICE 'end_pt = %', ST_AsText(end_pt);
	--RAISE NOTICE 'center = %', ST_AsText(center);
	--RAISE NOTICE 'angle = %', angle;

    SELECT ST_Centroid(result_geom) INTO tg_centroid;

    -- Step 11: Apply vertical offset using text_position (in meters)
    IF text_position IS NOT NULL AND text_position <> 0 THEN
        DECLARE
            srid_base INT := 4326;--ST_SRID(base_geom);
            srid_metric INT := 3857;
        BEGIN
            -- Transform to metric SRID, apply vertical offset, and transform back
            result_geom := ST_Transform(
                ST_Translate(
                    ST_Transform(ST_SetSRID(result_geom, srid_base), srid_metric),
                    0, text_position
                ),
                srid_base
            );
        END;
    END IF;

    -- Step 12: Rotate and translate result geometry
    result_geom := ST_Rotate(
        ST_Translate( result_geom, ST_X(center) - ST_X(tg_centroid), ST_Y(center) - ST_Y(tg_centroid)
		            ),
        CASE
            WHEN degrees(angle) BETWEEN 0 AND 180 THEN pi()/2 - angle
            ELSE (pi()/2 - angle) + pi()
        END,
        center
    );

    CALL font_source(FALSE); -- Turn off / Drop font table
    RETURN ST_SetSRID(result_geom, ST_SRID(base_geom)); -- Return the same SRID
END;
$$;





